import React, { useEffect } from "react";
import styles from "./ListCity.module.css";
import CityItem from "./CityItem";
const ListCity = function (props) {
  let dataBackEnd;
  const getDataHomePage = async function () {
    const response = await fetch("http://localhost:5000/home/listcity");
    const data = await response.json();
    dataBackEnd = data;
    console.log(dataBackEnd.find((c) => c.city === "Ho Chi Minh").properties);
  };
  useEffect(() => {
    getDataHomePage();
  }, []);

  const listCity = [
    {
      name: "Ha Noi",
      subText: `${
        dataBackEnd &&
        dataBackEnd.find((c) => c.city === "Ha Noi")[0].properties
      }properties`,
      image: "./images/HaNoi.jpg",
    },
    {
      name: "Ho Chi Minh",
      subText: `${
        dataBackEnd &&
        dataBackEnd.filter((c) => c.city === "Ho Chi Minh")[0].properties
      }properties`,
      image: "./images/HCM.jpg",
    },
    {
      name: "Da Nang",
      subText: "532 properties",
      image: "./images/DaNang.jpg",
    },
  ];
  // Trả ra JSX code gồm các city được render bằng cách gọi phương thức map cho array
  return (
    <div className={styles.cityList}>
      {listCity.map((city) => {
        return (
          <CityItem
            key={city.name}
            name={city.name}
            subText={city.subText}
            image={city.image}
          />
        );
      })}
    </div>
  );
};
export default ListCity;
