import { useEffect, useState } from "react";
import styles from "./ReserveInfor.module.css";

const ReserveInfor = () => {
  const [username, setUserName] = useState("");
  const [userFullName, setUserFullName] = useState("");
  const [userPhone, setUserPhone] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const getDataUser = async () => {
    const response = await fetch(`http://localhost:5000/detail/userinfor`, {
      method: "GET",
      mode: "cors",
      credentials: "include",
    });
    const data = await response.json();
    setUserName(dat.username);
    setUserFullName(data.fullName);
    setUserPhone(data);
    setUserEmail(data);
  };
  useEffect(() => {
    getDataUser();
  });
  return (
    <div className={styles.infor}>
      <h3>Reserve Info</h3>
      <form>
        <label htmlFor="fullName">Your Full Name:</label>
        <input id="fullName" placeholder="Full Name"></input>
        <label htmlFor="email">Your Email:</label>
        <input id="email" placeholder="Email"></input>
        <label htmlFor="phone">Your Phone Number:</label>
        <input id="phone" placeholder="Phone Number"></input>
        <label htmlFor="cardnumber">Your Your Identity Card Number:</label>
        <input id="cardnumber" placeholder="Card Number"></input>
      </form>
    </div>
  );
};
export default ReserveInfor;
